scan_table_check.script:
(function (engine) {

	if (current.active == false)
	{
		return;
	}

	var grGroupMember = new GlideRecord('sys_user_grmember');
	grGroupMember.addQuery('group','=',current.sys_id);
	grGroupMember.query();
	var bHasActiveUser = false;
	while (grGroupMember.next())
	{
		var grUser = x_snc_devtools.GetUserRecord(grGroupMember.user);
		if (grUser != false)
		{
			if (grUser.active == true)
			{
				bHasActiveUser = true;
			}
		}
	}

	if (bHasActiveUser == false)
	{
		engine.finding.increment();
	}

})(engine);
