scan_table_check.script:
(function (engine) {

	if (current.active == false)
	{
		return;
	}
	if (current.locked_out == true)
	{
		return;
	}
	if (x_snc_devtools.IsTechnicalUser(current) == true)
	{
		return;
	}
	if (x_snc_devtools.IsTestUser(current) == true)
	{
		return;
	}

	var grGroupMember = new GlideRecord('sys_user_grmember');
	grGroupMember.addQuery('user','=',current.sys_id);
	grGroupMember.query();
	var bActiveGroup = false;
	while (grGroupMember.next() && !bActiveGroup)
	{
		var grGroup = x_snc_devtools.GetRecord('sys_user_group',grGroupMember.group);
		if (x_snc_devtools.IsValidRecord(grGroup) == true)
		{
			if (grGroup.active == true)
			{
				bActiveGroup = true;
			}
		}
	}

	if (bActiveGroup == false)
	{
		engine.finding.increment();
	}



})(engine);
