sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	var strCheck = '9483a45c2b717a909083fe395e91bf07';
	
	var grUser = new GlideRecord('sys_user');
	grUser.initialize();
	grUser.user_name = 'test1';
	grUser.active = true;
	grUser.locked_out = false;
	var strUserSysId = x_snc_devtools.GlideRecordInsert(grUser);

	test.Log('User is active but not member of a group');
	test.TestCase_InstanceScanCheck(strCheck,'sys_user',strUserSysId,true);

	grUser.active = false;
	x_snc_devtools.GlideRecordUpdate(grUser);

	test.Log('User is inactive');
	test.TestCase_InstanceScanCheck(strCheck,'sys_user',strUserSysId,false);

	grUser.active = true;
	grUser.locked_out = true;
	x_snc_devtools.GlideRecordUpdate(grUser);

	test.Log('User is active but locked out');
	test.TestCase_InstanceScanCheck(strCheck,'sys_user',strUserSysId,false);

	grUser.locked_out = false;
	x_snc_devtools.GlideRecordUpdate(grUser);

	var grGroup = new GlideRecord('sys_user_group');
	grGroup.initialize();
	grGroup.user_name = 'TEST1';
	grGroup.active = true;
	var strGroupSysId = x_snc_devtools.GlideRecordInsert(grGroup);

	var grGroupMember = new GlideRecord('sys_user_grmember');
	grGroupMember.initialize();
	grGroupMember.group = strGroupSysId;
	grGroupMember.user = strUserSysId;
	x_snc_devtools.GlideRecordInsert(grGroupMember);

	test.Log('User is active and member of an active group');
	test.TestCase_InstanceScanCheck(strCheck,'sys_user',strUserSysId,false);

	grGroup.active = false;
	x_snc_devtools.GlideRecordUpdate(grGroup);

	test.Log('User is active and member of an inactive group');
	test.TestCase_InstanceScanCheck(strCheck,'sys_user',strUserSysId,true);

	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
